mapper.select:
SELECT product_name,SUM(product_quantity) total_count
FROM tb_order_detail



            create_time between #{startTime} and #{endTime}


            date_format(create_time,'%Y-%m')=#{month}



GROUP BY product_name
ORDER BY SUM(product_quantity) DESC LIMIT 5;

SELECT SUM(IF(order_status = 0,1,0)) 'finish', SUM(If(order_status = 1,1,0)) 'cancel', SUM(IF(pay_status = 0,1,0)) 'finish_pay', SUM(IF(pay_status = 1,1,0)) 'wait_pay' FROM tb_order
create_time between #{startTime} and #{endTime}
date_format(create_time,'%Y-%m')= #{month}
